import { useEffect, useState } from "react";
import { Header } from "../../components/header/header";
import "./home.css";
import phone from "../../assets/img/phone.png";
import posterPlaceholder from "../../assets/img/poster.png";
import like from "../../assets/img/like.png";
import dislike from "../../assets/img/dislike.png";
import { ProfileModal } from "../../components/profileModal/profileModal";


export const Home = ({ openProfileModal, setOpenProfileModal, userInfo }) => {
  const [movie, setMovie] = useState(null);
  const [poster, setPoster] = useState("");
  const [liked, setLiked] = useState(null);

  const fetchMovieWithPoster = async () => {
    let movieWithPoster = null;
    let posterUrl = "";

    try {
      const response = await fetch("http://localhost:8000/api/random-movie/");
      if (response.status === 403) {
        console.error("❌ Ошибка 403: Нет доступа.");
        return;
      }

      const data = await response.json();
      const movie = data?.docs?.[0];
      if (!movie || !movie.id) return;

      const imageResponse = await fetch(
        `http://localhost:8000/api/image/?movieId=${movie.id}&type=cover`
      );

      if (imageResponse.status === 403) {
        console.error("❌ Ошибка 403 при запросе изображения.");
        return;
      }

      const imageData = await imageResponse.json();

      if (imageData?.docs?.length > 0) {
        movieWithPoster = movie;
        posterUrl = imageData.docs[0].url;
      }
    } catch (err) {
      console.error("❌ Ошибка при попытке загрузки:", err);
    }

    if (movieWithPoster && posterUrl) {
      setMovie(movieWithPoster);
      setPoster(posterUrl);
    } else {
      console.warn("⚠️ Не удалось найти фильм с постером");
    }
  };

  // useEffect теперь вызывает fetchMovieWithPoster
  useEffect(() => {
    fetchMovieWithPoster(); // Вызываем функцию для загрузки фильма при монтировании компонента
  }, []);

  // useEffect(() => {
