  //     let posterUrl = "";

  //     try {
  //       const response = await fetch("http://localhost:8000/api/random-movie/");
  //       if (response.status === 403) {
  //         console.error("❌ Ошибка 403: Нет доступа.");
  //         return;
  //       }

  //       const data = await response.json();
  //       const movie = data;
  //       if (!movie || !movie.id) return;

  //       // const imageResponse = await fetch(
  //       //   `http://localhost:8000/api/image/?movieId=${movie.id}&type=cover`
  //       // );
  //       posterUrl = movie.poster || '';

  //     //   if (imageResponse.status === 403) {
  //     //     console.error("❌ Ошибка 403 при запросе изображения.");
  //     //     return;
  //     //   }

  //     //   const imageData = await imageResponse.json();

  //     // //   if (imageData?.docs?.length > 0) {
  //     //     movieWithPoster = movie;
  //     //     posterUrl = imageData.docs[0].url;
  //     //   }
  //       setMovie(movie);
  //       setPoster(posterUrl);

  //     } catch (err) {
  //       console.error("❌ Ошибка при попытке загрузки:", err);
  //     }

  //     if (movieWithPoster && posterUrl) {
  //       setMovie(movieWithPoster);
  //       setPoster(posterUrl);
  //     } else {
  //       console.warn("⚠️ Не удалось найти фильм с постером");
  //     }
  //   };


  //   fetchFilteredMovie();
  // }, []);

  const handleLike = () => {
    setLiked(true);
    fetchMovieWithPoster(); // Запрос на новый фильм и постер
  };

  const handleDislike = () => {
    setLiked(false);
    fetchMovieWithPoster(); // Запрос на новый фильм и постер
  };

  return (
    <div className="homePage">
      <Header setOpenProfileModal={setOpenProfileModal} />
      <div className="titleContainerHomePage">
        <div className="titleHomePage">Что посмотреть?</div>
        <div className="btnContainerHomePage">
          <div className="buttonLeftHomePage">
            Комедия
            <svg width="16" height="19" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M13.2046 3.76465L3.76465 13.2046" stroke="#69FF76" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              <path d="M3.76465 3.76465L13.2046 13.2046" stroke="#69FF76" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
            </svg>
          </div>
          <div className="buttonRightHomePage">
            Фильтр
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path
                d="M19.9201 8.9502L13.4001 15.4702C12.6301 16.2402 11.3701 16.2402 10.6001 15.4702L4.08008 8.9502"
                stroke="#292D32"
                strokeWidth="2"
                strokeMiterlimit="10"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </div>
        </div>
      </div>

      <div className="imgContainerPhoneHomePage">
        <img src={phone} className="imgPhoneHomePage" alt="phone" />
      </div>

      <div className="posterContainerPhoneHomePage">
        {poster ? (
          <img src={poster} className="posterPhoneHomePage" alt="Movie poster" />
        ) : (
          <img src={posterPlaceholder} className="posterPhoneHomePage" alt="Poster placeholder" />
        )}
      </div>

      <div className="likeBlockHomePage">
        <div className="likeContainerHomePage">
          <img src={dislike} className="likePhoneHomePage" alt="Dislike" onClick={handleDislike}/>
          <img src={like} className="likePhoneHomePage" alt="Like" onClick={handleLike} />
        </div>
      </div>

      {openProfileModal && (
        <div className="profileModalHomePage">
          <ProfileModal
            setOpenProfileModal={setOpenProfileModal}
            name={userInfo.name}
            surname={userInfo.surname}
            email={userInfo.email}
            nick={userInfo.nick}
          />
        </div>
      )}
    </div>
  );
};
