import { useEffect, useState } from "react";
import { Header } from "../../components/header/header";
import "./home.css";
import phone from "../../assets/img/phone.png";
import posterPlaceholder from "../../assets/img/poster.png";
import like from "../../assets/img/like.png";
import dislike from "../../assets/img/dislike.png";
import { ProfileModal } from "../../components/profileModal/profileModal";

// üîë –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ —Å–≤–æ–π API –∫–ª—é—á –ö–∏–Ω–æ–ø–æ–∏—Å–∫–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä, —Å kinopoisk.dev)
const API_KEY = "7RGCJNG-ZQX4PD3-QSDG2T1-7Z49BNE";

export const Home = ({ openProfileModal, setOpenProfileModal, userInfo }) => {
  const [movie, setMovie] = useState(null);
  const [poster, setPoster] = useState("");

  const fetchRandomMovie = async () => {
    try {
      const response = await fetch("https://api.kinopoisk.dev/v1.4/movie/random", {
        method: "POST",
        headers: {
          "X-API-KEY": API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query: {
            "rating.kp": { "$gte": 6 },           // –†–µ–π—Ç–∏–Ω–≥ –ö–∏–Ω–æ–ø–æ–∏—Å–∫–∞ ‚â• 6
            "languages": { "$in": ["—Ä—É—Å—Å–∫–∏–π", "–∞–Ω–≥–ª–∏–π—Å–∫–∏–π"] }, // –Ø–∑—ã–∫–∏
            "type": "movie",                      // –¢–æ–ª—å–∫–æ —Ñ–∏–ª—å–º—ã
          }
        }),
      });

      const movieData = await response.json();
      const movieId = movieData.id;

      console.log("üé¨ Movie data:", movieData);

      const imageResponse = await fetch(
        `https://api.kinopoisk.dev/v1.4/image?movieId=${movieId}`,
        {
          headers: {
            "X-API-KEY": API_KEY,
            "Content-Type": "application/json",
          },
        }
      );

      const imageData = await imageResponse.json();
      console.log("üñºÔ∏è Image data:", imageData);

      if (imageData.docs.length === 0) {
        console.warn("‚õî No images found, fetching another movie...");
        fetchRandomMovie();
        return;
      }

      const validImage = imageData.docs.find(img => img.url);
      if (validImage) {
        setPoster(validImage.url);
      } else {
        console.warn("‚ö†Ô∏è No valid image URL found, retrying...");
        fetchRandomMovie();
        return;
      }

      setMovie(movieData);
    } catch (error) {
      console.error("‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ñ–∏–ª—å–º–∞:", error);
    }
  };
    fetchRandomMovie();


  return (
    <div className="homePage">
      <Header setOpenProfileModal={setOpenProfileModal} />
      <div className="titleContainerHomePage">
        <div className="titleHomePage">–ß—Ç–æ –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å?</div>
        <div className="btnContainerHomePage">
          <div className="buttonLeftHomePage">
            –ö–æ–º–µ–¥–∏—è
            <svg width="16" height="19" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M13.2046 3.76465L3.76465 13.2046" stroke="#69FF76" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              <path d="M3.76465 3.76465L13.2046 13.2046" stroke="#69FF76" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
            </svg>
          </div>
          <div className="buttonRightHomePage">
            –§–∏–ª—å—Ç—Ä
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path
                d="M19.9201 8.9502L13.4001 15.4702C12.6301 16.2402 11.3701 16.2402 10.6001 15.4702L4.08008 8.9502"
                stroke="#292D32"
                strokeWidth="2"
                strokeMiterlimit="10"
                strokeLinecap="round"
                strokeLinejoin="round"
              />
            </svg>
          </div>
        </div>
      </div>

      <div className="imgContainerPhoneHomePage">
        <img src={phone} className="imgPhoneHomePage" alt="phone" />
      </div>

      <div className="posterContainerPhoneHomePage">
        {poster ? (
          <img src={poster} className="posterPhoneHomePage" alt="Movie poster" />
        ) : (
          <img src={posterPlaceholder} className="posterPhoneHomePage" alt="Poster placeholder" />
        )}
      </div>

      <div className="likeBlockHomePage">
        <div className="likeContainerHomePage">
          <img src={dislike} className="likePhoneHomePage" alt="Dislike" />
          <img src={like} className="likePhoneHomePage" alt="Like" />
        </div>
      </div>

      {openProfileModal && (
        <div className="profileModalHomePage">
          <ProfileModal
            setOpenProfileModal={setOpenProfileModal}
            name={userInfo.name}
            surname={userInfo.surname}
            email={userInfo.email}
            nick={userInfo.nick}
          />
        </div>
      )}
    </div>
  );
};
